import React  from 'react';
import Clock from 'react-live-clock';
import { Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

class Index extends React.Component {
    constructor(props) {
          this.startTimer = this.startTimer.bind(this)
          this.stopTimer = this.stopTimer.bind(this)
          this.resetTimer = this.resetTimer.bind(this)
    }

    startTimer() {
        this.setState({
          isOn: true,
          time: this.state.time,
          start: Date.now() - this.state.time
        })
        this.timer = setInterval(() => this.setState({
          time: Date.now() - this.state.start
        }), 1);
      }


      stopTimer() {
        this.setState({isOn: false})
        clearInterval(this.timer)
      }

      resetTimer() {
        this.setState({time: 0, isOn: false})
      }


    setTime(){

        let currentdate = new Date();
        let hours = currentdate.getUTCHours()+2    


          if( hours >= 24 ){ hours -= 24; }
          if( hours < 0   ){ hours += 12; }

          hours = hours + "";
          if( hours.length == 1 ){ hours = "0" + hours; }


          let minutes = currentdate.getUTCMinutes();


          minutes = minutes + "";
          if( minutes.length == 1 ){ minutes = "0" + minutes; }

          let seconds = currentdate.getUTCSeconds();
          //console.log(hours, minutes, seconds)
          this.setState({
            hours: hours,
            minutes: minutes,
            seconds: seconds,
            time: 0,
            isOn: false,
            start: 0
          });
      }

      timerstart() {
        this.setState(
            (state, props) => {
              return { timer: state.counter + 1 };
            });
      }

      componentWillMount(){
        this.setTime();
      }

      componentDidMount(){
        window.setInterval(function () {
         this.setTime();
       }.bind(this), 1000);
     }



    render() {
        let start = (this.state.time == 0) ?
      <button onClick={this.startTimer}>start</button> :
      null
    let stop = (this.state.time == 0 || !this.state.isOn) ?
      null :
      <button onClick={this.stopTimer}>stop</button>
    let resume = (this.state.time == 0 || this.state.isOn) ?
      null :
      <button onClick={this.startTimer}>resume</button>
    let reset = (this.state.time == 0 || this.state.isOn) ?
      null :
      <button onClick={this.resetTimer}>reset</button>

        return (<React.Fragment>
            <Clock format={'HH:mm:ss'} ticking={true} timezone={'US/Pacific'} /><br/>
            <Button color="primary"  >Start</Button>
            <Button color="danger">Stop</Button>
            <Button color="success">Reset</Button>

            <div >
                <label>Real - Time : </label>
                <span style= {{"textDecoration":"underline"}} >{this.state.hours}:{this.state.minutes}:{this.state.seconds}</span>
            </div>


            <h3>timer: {ms(this.state.time)}</h3>
            {start}
            {resume}
            {stop}
            {reset}
        </React.Fragment>)
    }
}



export default Index
